<?php	session_start();	require("conexion.php");	$status = "";	$desc = $_POST["maquina"];	if (isset($_GET['editar'])) {		if ($_POST['estado'] == 'Activo') {			$sql = "UPDATE `subproceso_maq` SET `anchom` = '" . $_POST['ancho'] . "',`altom` = '" . $_POST['alto'] . "',`esp1` = '" . $_POST['esp1'] . "',`esp2` = '" . $_POST['esp2'] . "',`nombre_maq` = '" . $desc . "', `id_g` = '" . $_POST['id_grupo'] . "', `estado_maq` = '" . $_POST["estado"] . "', `observaciones` = '" . strtoupper($_POST['observaciones']) . "' WHERE `id_spm` = " . $_GET["editar"] . ";";		} else {			$sql = "UPDATE `subproceso_maq` SET `anchom` = '" . $_POST['ancho'] . "',`altom` = '" . $_POST['alto'] . "',`esp1` = '" . $_POST['esp1'] . "',`esp2` = '" . $_POST['esp2'] . "',`nombre_maq` = '" . $desc . "', `id_g` = '0', `estado_maq` = '" . $_POST["estado"] . "', `observaciones` = '" . strtoupper($_POST['observaciones']) . "' WHERE `id_spm` = " . $_GET["editar"] . ";";		}		mysql_query($sql, $conexion);		echo '<script lanquage="javascript">alert("Se ha Editado exitosamente");location.href="../vistas/?id=maq&cod='.$_GET['cod'].' "</script>';	} else {		$sqm = mysql_query("SELECT MAX(paso_maq) FROM subproceso_maq WHERE id_subproceso = " . $_GET['cod'] . " ");		$m = mysql_fetch_array($sqm);		$paso = $m['MAX(paso_maq)'] + 1;		$sql = "INSERT INTO `subproceso_maq` (`anchom`,`altom`,`esp1`,`esp2`,`nombre_maq`, `id_subproceso`, `estado_maq`, `paso_maq`, `observaciones`)";		$sql.= "VALUES ('" . $_POST['ancho'] . "','" . $_POST['alto'] . "','" . $_POST['esp1'] . "','" . $_POST['esp2'] . "', '" . $desc . "' , '" . $_GET['cod'] . "', 'Activo', '" . $paso . "', '" . $_POST['observaciones'] . "')";		mysql_query($sql, $conexion);		$status = "ok";		echo '<script lanquage="javascript">alert("Se ha Guardado exitosamente");location.href="../vistas/?id=maq&cod='.$_GET['cod'].' "</script>';	}?>