<?php      if(isset($_GET['up_1'])){        $sql21 = "SELECT * FROM referencias where id_referencia=".$_GET['up_1'];        $fila21 =mysql_fetch_array(mysql_query($sql21));            $ref= $fila21["referencia"];            $des= $fila21["descripcion"];            $cod= $fila21["codigo"];            $cos= $fila21["costo_mt"];$cos2= $fila21["costo_fom"];            $med= $fila21["medida"];            $und_med= $fila21["und_medida"];            $cant= $fila21["cantidad_e"];            $linea= $fila21["grupo"];            $dolar= $fila21["act_dolar"];            $peso= $fila21["peso"];            $aumento= $fila21["aumento"];            $uti= $fila21["utilidad"];            $max= $fila21["max_desc"];            $dado= $fila21["dado"];    }    $sq = "SELECT precio_actual FROM `dolares` order by id_dolar desc limit 1 ";        $dr =mysql_fetch_array(mysql_query($sq));        $baseprecio = $dr[0];?> <script> $(document).ready(function(){    buscar_referencias(1);        $("#refe").change(function () {        buscar_referencias(1);			    });            $("#grupo").change(function () {        buscar_referencias(1);    });    });function calculovalor(){    var linea = $('#fgru').val();    var peso = $('#fpes').val();    var peri = $('#fper').val();    var base = $('#fbase').val();    var valor = $('#fval').val();    var t;    if(linea=='Perfileria'){        t = peso * base;        $('#fval').val(t);        $('#fbas').val(base);    }else{        $('#fbas').val(valor);    }    }function dado(id){    var d = $("#dado"+id).val();     var p = $("#peso"+id).val();     var a = $("#area"+id).val();     var page = $("#page").val();    $.ajax({        type: 'GET',        data: 'id='+id+'&dado='+d+'&peso='+p+'&area='+a,        url: '../modelo/dado.php',        success: function(){            $('#d'+id).html('');            buscar_referencias(page);        }    });}function buscar_referencias(page){    var grupo = $('#grupo').val();    var buscador = $('#refe').val();$.ajax({        type: 'POST',        data: 'buscador='+buscador+'&grupo='+grupo+'&page='+page,        url: '../vistas/referencias/buscador_refe.php',        success: function(D){            $("#res_referencias").html(D);        }    });            }function ver_referencia(cod){    $('#fcod').val(cod);    verificarcod();    $("#tab1").tabs({ active: 1 });    $("#tab2").tabs({ active: 0 });}function verificarcod(){    var cod = $('#fcod').val();      $.ajax({        type: 'POST',        data: 'cod='+cod+'&sw=1',        url: '../vistas/referencias/acciones.php',        success: function(D){            var p = eval(D);            $('#fdes').val(p[1]);            $('#fref').val(p[0]);            $('#fid').val(p[16]);            $('#fcan').val(p[6]);            $('#fdad').val(p[13]);            $('#fpes').val(p[9]);            $('#fgru').val(p[7]);            $('#fmed').val(p[4]);            $('#fund').val(p[5]);            $('#fbas').val(p[14]);            $('#fval').val(p[3]);            $('#futi').val(p[11]);            $('#fmax').val(p[12]);            $('#fper').val(p[15]);             $('#fdol').val(p[8]);        }    });}function buscarfom(){     var cod = $("#fcod").val();    if(cod==''){        alert("Digite el codigo!");        $("#codxa").focus();        return false;    }      $.ajax({          type:'GET',          //data: JSON.stringify(datos),          url:'http://172.16.0.30:8989/api/MAEINV/'+cod,          contentType: 'application/json',          success: function(da){             var p = eval(da);              console.log('Resultado: '+p.INV_IVA);               $("#fcod").val(cod);              $("#fref").val(p.INV_CODIGO);              $("#fdes").val(p.INV_NOMBRE);               $("#fcol").val(p.INV_LOTE);              $("#fmed").val(p.INV_ANCHO);               if(p.INV_IVA==true){                   $("#faiva").val('true');               }else{                   $("#faiva").val('false');               }                           $("#fiva").val(p.INV_EQUIVA);              $("#fpes").val(p.INV_PESO);              $("#fcan").val(p.INV_STCMAX);                         $("#fval").val(p.INV_VALCOM);              $("#fobs").val(p.INV_CODOPE);                $("#fcla").val(p.INV_CLASE);                $("#fund").val(p.INV_UNDMED);               $('#fdol').val('Si');//              $("#poriva_xa").val(p.INV_BASIVA);               //$("#iva_xa").val(p.INV_IVA);                        }      }).fail( function( jqXHR, textStatus, errorThrown ) {             alert( 'Este codigo no esta registrado en fom plus\n Comuniquese con el area de inventario para crear el producto' );//           $("#est"+ced).html('');             return false;        });  }</script>    <div class="row-fluid">                        <!-- START Form Wizard -->                      <!-- START Widget Collapse -->                           <section class="body">                                <div class="body-inner">                        <div class="span12 widget dark stacked">                            <header>                                <h4 class="title">Listado de Materiales</h4>                                <!-- START Toolbar -->                                <ul class="toolbar pull-left">                                    <li><a class="link" data-toggle="collapse1" href="#collapse1"><span class="icon icone-chevron-up"></span></a></li>                                </ul>                                <!--/ END Toolbar -->                            </header>                            <section id="collapse2" class="body collapse in">                                <div class="body-inner">                                                                               <!-- Normal Tabs -->                                                        <div class="tabbable" style="margin-bottom: 25p