'#fpes').val();    var peri = $('#fper').val();    var base = $('#fbase').val();    var valor = $('#fval').val();    var t;    if(linea=='Perfileria'){        t = peso * base;        $('#fval').val(t);        //$('#fbas').val(base);    }else{        //$('#fbas').val(valor);    }    }function dado(id){    var d = $("#dado"+id).val();     var p = $("#peso"+id).val();     var a = $("#area"+id).val();     var page = $("#page").val();    $.ajax({        type: 'GET',        data: 'id='+id+'&dado='+d+'&peso='+p+'&area='+a,        url: '../modelo/dado.php',        success: function(){            $('#d'+id).html('');            buscar_referencias(page);        }    });}function buscar_referencias(page){    var grupo = $('#grupo').val();    var buscador = $('#refe').val();    var perimetro = $('#perimetro').val();$.ajax({        type: 'POST',        data: 'buscador='+buscador+'&grupo='+grupo+'&perimetro='+perimetro+'&page='+page,        url: '../vistas/referencias/buscador_refe.php',        success: function(D){            $("#res_referencias").html(D);        }    });            }function ver_referencia(cod){    $('#fcod').val(cod);    verificarcod();    $("#tab1").tabs({ active: 1 });    $("#tab2").tabs({ active: 0 });}function verificarcod(){    var cod = $('#fcod').val();      $.ajax({        type: 'POST',        data: 'cod='+cod+'&sw=1',        url: '../vistas/referencias/acciones.php',        success: function(D){            var p = eval(D);            $('#fdes').val(p[1]);            $('#fref').val(p[0]);            $('#fid').val(p[16]);            $('#fcan').val(p[6]);            $('#fdad').val(p[13]);            $('#fpes').val(p[9]);                        $('#fmed').val(p[4]);            $('#fund').val(p[5]);            $('#fbas').val(p[14]);            $('#fbase').val(p[14]);            $('#fval').val(p[3]);            $('#futi').val(p[11]);            $('#fmax').val(p[12]);            $('#fper